import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSquare} from "@fortawesome/free-solid-svg-icons";
import "./Task.css"

export const Task = (props) => {
    const {id} = useParams();
    const [task, setTask] = useState({})

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.tasks).fetchById(id)
            .then(res => {
                setTask(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    },[id])

    const taskStatus = {
        0: "Pending",
        1: "In Progress",
        2: "Completed",
    }

    const getTaskStatusColor = (status) => {
        switch (status) {
            case 0:
                return "#ED2939";
            case 1:
                return "#F5DE10";
            case 2:
                return "#29AB87";
            default:
                return "#FFFFFF";
        }
    };

    return (
        <div className="container d-flex flex-column flex-grow-1 white-box-task pb-4 px-5 top-margin">
            <div className="my-4">
                <h3 className="dark-blue">
                    <FontAwesomeIcon icon={faSquare} style={{color:getTaskStatusColor(task.status)}} />
                    &nbsp;{task.description}</h3>
                <p className="light-blue">Created on {task.createdAt} by {task.creator?.userName}</p>
            </div>
            <div className="d-flex flex-row">
                <div className="w-75">
                    <p>Comments go here</p>
                </div>
                <div className="w-25">
                    <p>Users and task status go here</p>
                </div>
            </div>

        </div>
    )
}
