import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Task.css"

export const Task = (props) => {
    const {id} = useParams();
    const [task, setTask] = useState({})

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.tasks).fetchById(id)
            .then(res => {
                setTask(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    },[id])

    const taskStatus = {
        0: "Pending",
        1: "In Progress",
        2: "Completed",
    }

    return (
        <div className="container d-flex flex-column flex-grow-1 white-box-task pb-4 px-5 top-margin">
            <div className="my-4">
                <h3>{task.description}</h3>
                <p>Created on {task.createdAt} by {task.creator?.userName}</p>
            </div>
        </div>
    )
}
