import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./UserTasks.css"

export const UserTasks = (props) => {
    const {id} = useParams();
    const [userTasks, setUserTasks] = useState({})

    useEffect(() => {
        const headers = {"Authorization": "Bearer " + sessionStorage.getItem("token")}
        createAPIEndpoint(ENDPOINTS.userTasks,headers).fetchById(id)
            .then(res => {
                setUserTasks(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    }, [id])

    return (
        <div className="container d-flex justify-content-between flex-row flex-grow-1 white-box-task pb-4 px-5 top-margin">
            <div className="w-30">
                Pending
            </div>
            <div className="w-30">
                In Progress
            </div>
            <div className="w-30">
                Completed
            </div>
        </div>
    )
}
