import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./User.css"
import Select from "react-select";

export const User = (props) => {
    const {id} = useParams();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const headers = {"Authorization": "Bearer " + sessionStorage.getItem("token")}
        createAPIEndpoint(ENDPOINTS.userProfile, headers).fetchById(id)
            .then(res => {
                setUser(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    }, [id])

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    return (
        <div className="container top-margin">
            {user &&
                <div className="d-flex flex-row justify-content-center align-items-top gap-5">
                    <div style={{width: 25 + "%"}}>
                        <div className="card text-center">
                            <div className="card-body">
                                <img src={user.profilePictureSrc} alt={user.userName}
                                     className="avatar"/>
                                <h4 className="card-title dark-blue">{user.userName}</h4>
                                <h6 className="card-text dark-blue">{jobTitles[user.jobTitle]}</h6>
                            </div>
                        </div>
                        <div className="half-circle">
                            <div className="band"></div>
                            <div className="band"></div>
                            <div className="band"></div>
                        </div>
                    </div>
                    <div style={{width: 75 + "%"}}>
                        <div className="card">
                            <div className="card-body">
                                <p><strong>First Name:</strong> {user.firstName}</p>
                                <p><strong>Last Name:</strong> {user.lastName}</p>
                            </div>
                        </div>

                        <p><strong>Statistics:</strong></p>
                        <p><strong>Number of Projects:</strong> {user.numProjects}</p>
                        <p><strong>Number of Tasks:</strong> {user.numTasks}</p>
                        <p><strong>Number of Comments:</strong> {user.numComments}</p>
                    </div>
                </div>
            }
        </div>
    )
}
