import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Home.css"

export const Home = (props) => {
    const userId = sessionStorage.getItem("userId")
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [userProfile, setUserProfile] = useState({});
    const [isProjectManager, setIsProjectManager] = useState(false);
    const users = allUsers.filter(user => user.id !== userId);

    const jobTitles = {
        Developer : 0,
        Tester : 1,
        Designer : 2,
        BusinessAnalyst : 3,
        ProjectManager : 4,
        Other: 5
    }

    useEffect(() =>{
        createAPIEndpoint(ENDPOINTS.userProjects).fetchById(userId)
            .then(res => {
                setUserProjects(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetch()
            .then(res => {
                setAllUsers(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetchById(userId)
            .then(res => {
                setUserProfile(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

    },[userId])


    return (
        <div>
            <div className="row mx-0" style={{height:750+"px"}}>
                <div className="col-3 bg-primary">
                    <div className="text-center">
                        <img src={"/img/"+userProfile.profilePictureSrc} alt={userProfile.userName} className="avatar" />
                        <h3>{userProfile.userName}</h3>
                        <h5 className="text-muted">{userProfile.firstName} {userProfile.lastName}</h5>
                        <h6>{jobTitles[userProfile.jobTitle]}</h6>
                    </div>
                </div>
                <div className="col-7 bg-secondary">
                    &nbsp;
                </div>
                <div className="col-2 bg-danger">
                    <div className="mt-3">
                        <h4>Project users:</h4>
                    </div>
                    <div className="user-list">
                        {users.map(user => (
                            <div key={user.id} className="user-row">
                                <img src={"/img/"+user.profilePictureSrc} alt={user.userName} className="user-avatar" />
                                <p className="user-name">{user.userName}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}
