import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Home.css"

export const Home = (props) => {
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [userProfile, setUserProfile] = useState({});
    const [isProjectManager, setIsProjectManager] = useState(false);
    const users = allUsers.filter(user => user.id !== sessionStorage.getItem("userId"));

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.userProjects).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProjects(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetch()
            .then(res => {
                setAllUsers(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProfile(res.data)
                setIsProjectManager(res.data.jobTitle === 4)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    const handleSubmit = (e) => {

    }


    return (
        <div>
            <div className="row mx-0">
                <div className="col-md-3 p-3">
                    <div className="card text-center">
                        <div className="card-body">
                            <img src={userProfile.profilePictureSrc} alt={userProfile.userName}
                                 className="avatar"/>
                            <h4 className="card-title dark-blue">{userProfile.userName}</h4>
                            <h6 className="card-text dark-blue">{jobTitles[userProfile.jobTitle]}</h6>
                            <div className="my-5">
                                {isProjectManager &&
                                    <div>
                                        {/* Step 2: Button to open the modal */}
                                        <button className="btn pill-btn mx-auto" onClick={openModal}>
                                            + Add Project
                                        </button>
                                        {/* Step 3: Modal */}
                                        {showModal && (
                                            <div className="modal" tabIndex="-1" role="dialog">
                                                <div className="modal-dialog" role="document">
                                                    <div className="modal-content">
                                                        <div className="modal-header">
                                                            <h5 className="modal-title">Add Project</h5>
                                                            <button type="button" className="close" onClick={closeModal}>
                                                                <span aria-hidden="true">&times;</span>
                                                            </button>
                                                        </div>
                                                        <div className="modal-body">
                                                            {/* Step 3: Form for adding a new project */}
                                                            <form onSubmit={handleSubmit}>
                                                                <div className="form-group">
                                                                    <label htmlFor="projectTitle">Project Title</label>
                                                                    <input
                                                                        type="text"
                                                                        className="form-control"
                                                                        id="projectTitle"
                                                                        value={newProjectTitle}
                                                                        onChange={(e) => setNewProjectTitle(e.target.value)}
                                                                        required
                                                                    />
                                                                </div>
                                                                {/* You can add form fields for selecting members here */}
                                                                {/* For example, a multi-select dropdown */}
                                                                <div className="form-group">
                                                                    <label htmlFor="selectedMembers">Select Members</label>
                                                                    {/* Add your member selection UI here */}
                                                                </div>
                                                                <button type="submit" className="btn btn-primary">
                                                                    Save Project
                                                                </button>
                                                            </form>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                }
                                {!isProjectManager &&
                                    <p>&nbsp;</p>
                                }
                            </div>

                        </div>
                    </div>
                </div>
                <div className="col-md-7 p-3">
                    <h3 className="dark-blue">Projects:</h3>
                    {userProjects &&
                        <div>
                            {userProjects.map((project) => (
                                <div key={project.id}>
                                    <h5>{project.title}</h5>
                                    <p>Started: {project.dateStarted}</p>
                                    <h6>Assigned Users:</h6>
                                    <div className="user-list">
                                        {project.projectMembers.map((projectMember) => (
                                            // Render user profile picture here
                                            <img
                                                key={projectMember.id}
                                                src={projectMember.user.profilePictureSrc}
                                                alt={projectMember.user.userName}
                                                className="user-avatar"
                                            />
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    }
                </div>
                <div className="col-md-2 p-3">
                    <div className="list mx-0">
                        <div className="px-3">
                            <h4 className="pt-3 dark-blue"><b>Users:</b></h4>
                        </div>
                        <div className="user-list px-4 pb-5">
                            {users.map(user => (
                                <div key={user.id} className="user-row dark-blue">
                                    <img src={user.profilePictureSrc} alt={user.userName}
                                         className="user-avatar"/>
                                    <p className="user-name">{user.userName}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
