import {Link, useParams} from "react-router-dom";
import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSquare} from "@fortawesome/free-solid-svg-icons";
import "./Task.css"

export const Task = (props) => {
    const {id} = useParams();
    const [task, setTask] = useState({})
    const [commentData, setCommentData] = useState({
        
    })

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.tasks).fetchById(id)
            .then(res => {
                setTask(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    }, [id])

    const taskStatus = {
        0: "Pending",
        1: "In Progress",
        2: "Completed",
    }

    const getTaskStatusColor = (status) => {
        switch (status) {
            case 0:
                return "#ED2939";
            case 1:
                return "#F5DE10";
            case 2:
                return "#29AB87";
            default:
                return "#FFFFFF";
        }
    };

    return (
        <div className="container d-flex flex-column flex-grow-1 white-box-task pb-4 px-5 top-margin">
            <div className="my-4">
                <h3 className="dark-blue">
                    <FontAwesomeIcon icon={faSquare} style={{color: getTaskStatusColor(task.status)}}/>
                    &nbsp;{task.description}</h3>
                <p className="light-blue">Created on <b>{task.createdAt}</b> by <b>{task.creator?.userName}</b></p>
            </div>
            <div className="d-flex flex-row">
                <div className="w-75">
                    <h5>Comment:</h5>
                    <form>
                        <div className="input-group mb-3">
                            <textarea
                                className={"form-control input-border"}
                                id="comment"
                                name="comment"
                                placeholder="Comment..."
                                value={taskData.description}
                                onChange={handleChange}
                                rows="4"
                                required
                            />
                            <button className="btn btn-outline-secondary" type="button" id="button-addon2">Send
                            </button>
                        </div>

                    </form>

                </div>
                <div className="w-75">
                    <div className="card comment-card ms-2 me-5 my-2">
                        <div className="card-header comment-header">
                            <div className="d-flex justify-content-between align-items-center">
                                <div className="ps-2 cream">
                                    <b>
                                        John Smith
                                    </b>
                                </div>
                                <div className="pe-2 cream">
                                    14 September 2023 20:39
                                </div>
                            </div>
                        </div>
                        <div className="card-body comment-body">
                            <p className="card-text dark-blue">This is a comment for the task This is a comment for the task This is a comment for the task This is a comment for the task This is a comment for the taskThis is a comment for the task This is a comment for the taskvvv This is a comment for the taskvvvv vThis is a comment for the task</p>
                        </div>
                    </div>
                </div>
                <div className="w-25">
                    <div>
                        <div className="px-3 pt-3">
                            <h5 className="dark-blue"><b>Task status:</b></h5>
                        </div>
                        <div className="px-4">
                            <h5 style={{color: getTaskStatusColor(task.status)}}>{taskStatus[task.status]}</h5>
                        </div>
                    </div>
                    <div>
                        <div className="px-3 pt-3">
                            <h5 className="dark-blue"><b>Last Updated:</b></h5>
                        </div>
                        <div className="px-3">
                            <h6 className="light-blue">{task.updatedAt}</h6>
                        </div>
                    </div>
                    <div>
                        <div className="px-3 py-3">
                            <h5 className="dark-blue"><b>Members assigned to this task:</b></h5>
                        </div>
                        <div className="d-flex justify-content-center flex-column flex-wrap px-3 pb-5">
                            {task.taskMembers && task.taskMembers.map(user => (
                                <Link className="user-link" to={"/users/" + user.id} key={user.id}>
                                    <div
                                        className="user-row d-flex justify-content-start align-items-center dark-blue mx-auto gap-2">
                                        <img src={user.profilePictureSrc} alt={user.userName}
                                             className="user-avatar"/>
                                        <p className="user-name">{user.userName}</p>
                                    </div>
                                </Link>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

        </div>
    )
}
