import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Home.css"
import Select from 'react-select';
import {Link} from "react-router-dom";

export const Home = (props) => {
    const {createProject} = props
    const [errors, setErrors] = useState({})
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [userProfile, setUserProfile] = useState({});
    const [isProjectManager, setIsProjectManager] = useState(false);
    const users = allUsers.filter(user => user.id !== sessionStorage.getItem("userId"));
    const [projectData, setProjectData] = useState({
        title: '',
        projectMembers: [],
    });

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    const userOptions = users.map(user => ({
        value: user.id,
        label: user.userName,
    }));

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.userProjects).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProjects(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetch()
            .then(res => {
                setAllUsers(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProfile(res.data)
                setIsProjectManager(res.data.jobTitle === 4)
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    const handleChange = (e) => {
        const {name, value} = e.target;
        setProjectData({...projectData, [name]: value});
    };

    const handleUserSelectChange = (selectedOptions) => {
        setProjectData({...projectData, projectMembers: selectedOptions})
    };

    const validate = () => {
        let temp = {}
        temp.title = projectData.title !== "";
        setErrors(temp)
        return Object.values(temp).every(x => x === true)
    }

    const resetForm = () => {
        setProjectData({
            title: '',
            projectMembers: [],
        })
        setErrors({})
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if (validate()) {
            const data = new FormData()
            data.append("title", projectData.title)
            projectData.projectMembers.forEach((member) => {
                data.append("projectMembers", member.value);
            });
            data.append("projectMembers", sessionStorage.getItem("userId"));
            createProject(data,resetForm)
        }
    }

    const customStyles = {
        control: (provided, state) => ({
            ...provided,
            border: '2px solid #5F9DF7',
            borderRadius: '0.375rem',
            ':focus, :active': {
                boxShadow: '0 0 0 0.25rem rgba(23, 70, 162,0.25) !important',
                border: '2px solid #1746A2'
            },
        }),
    };

    const applyErrorClass = (field) => ((field in errors && errors[field] === false) ? " invalid" : "")

    return (
        <div>
            <div className="d-flex justify-content-around align-items-center gap-5">
                <div className="bg-primary" style={{width:22+"%",height:500+"px"}}>&nbsp;</div>
                <div className="bg-secondary" style={{width:40+"%",height:500+"px"}}>&nbsp;</div>
                <div className="bg-danger" style={{width:20+"%",height:500+"px"}}>&nbsp;</div>
            </div>
        </div>
    )
}
