import {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./User.css"

export const User = (props) => {
    const {id} = useParams();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const headers = {"Authorization": "Bearer " + sessionStorage.getItem("token")}
        createAPIEndpoint(ENDPOINTS.userProfile, headers).fetchById(id)
            .then(res => {
                setUser(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });
    }, [id])

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    return (
        <div className="container top-margin white-box-profile">
            {user &&
                <div>
                    <div className="row py-4">
                        <div className="col-md-4">
                            <img src={user.profilePictureSrc} alt="Profile" className="img-fluid rounded-circle"/>
                            <h2>{user.userName}</h2>
                        </div>
                        <div className="col-md-8">
                            <p><strong>First Name:</strong> {user.firstName}</p>
                            <p><strong>Last Name:</strong> {user.lastName}</p>
                            <p><strong>Job Title:</strong> {jobTitles[user.jobTitle]}</p>
                            <p><strong>Statistics:</strong></p>
                            <p><strong>Number of Projects:</strong> {user.numProjects}</p>
                            <p><strong>Number of Tasks:</strong> {user.numTasks}</p>
                            <p><strong>Number of Comments:</strong> {user.numComments}</p>
                        </div>
                    </div>
                </div>
            }
        </div>
    )
}
