import './App.css';
import {Route, BrowserRouter as Router, Routes, useLocation} from "react-router-dom";
import {Register} from "./components/Register/Register";
import Project from "./components/Project";
import {Login} from "./components/Login/Login";
import {createAPIEndpoint, ENDPOINTS} from "./api/axios";
import {Home} from "./components/Home/Home";
import {Auth} from "./components/Auth/Auth";
import {NavBar} from "./components/NavBar/NavBar";

function App() {
    const location = useLocation();

    const register = (formData, onSuccess) => {
        createAPIEndpoint(ENDPOINTS.register).post(formData)
            .then(res => {
                const token = res.data.token;
                const id = res.data.id;
                sessionStorage.setItem("token", token);
                sessionStorage.setItem("userId",id)
            })
            .then(res => {
                onSuccess(res)
            })
            .catch(err => {
                console.log(err)
            })
    }

    const logIn = (formData, onSuccess) => {
        createAPIEndpoint(ENDPOINTS.login).post(formData)
            .then(res => {
                const token = res.data.token;
                const id = res.data.id;
                sessionStorage.setItem("token", token);
                sessionStorage.setItem("userId", id);
                onSuccess(res);
            })
            .catch(err => {
                console.log(err);
            });
    }

    const isRegisterOrLogin = location.pathname === '/register' || location.pathname === '/login';

    return (
        <Router>
            <LocationCheck>
                <NavBar />
            </LocationCheck>
            <div>
                <Routes>
                    <Route path={"/register"} element={<Register register={register}/>}/>
                    <Route path={"/login"} element={<Login logIn={logIn}/>}/>
                    <Route path={"/project"} element={<Auth><Project/></Auth>}/>
                    <Route path={"/home"} element={<Auth><Home/></Auth>}/>
                    <Route path={"/"} element={<Auth><Home/></Auth>}/>
                </Routes>
            </div>
        </Router>
    )
}

export default App;
