import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Home.css"

export const Home = (props) => {
    const userId = sessionStorage.getItem("userId")
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [userProfile, setUserProfile] = useState({});
    const [isProjectManager, setIsProjectManager] = useState(false);
    const [loading, setLoading] = useState(false);
    const users = allUsers.filter(user => user.id !== userId);
    setIsProjectManager(userProfile.jobTitle === 4)

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.userProjects).fetchById(userId)
            .then(res => {
                setUserProjects(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetch()
            .then(res => {
                setAllUsers(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetchById(userId)
            .then(res => {
                setUserProfile(res.data)
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            });

    }, [userId])

    const fetchData = () => {
        
    }


    return (
        <div>
            <div className="row mx-0">
                <div className="col-md-3 p-3">
                    <div className="card text-center">
                        <div className="card-body">
                            <img src={userProfile.profilePictureSrc} alt={userProfile.userName}
                                 className="avatar"/>
                            <h4 className="card-title">{userProfile.userName}</h4>
                            <h6 className="card-text">{jobTitles[userProfile.jobTitle]}</h6>

                            {isProjectManager &&
                                <div>
                                    <button className="btn pill-btn">+ Add Project</button>
                                </div>
                            }
                        </div>
                    </div>
                </div>
                <div className="col-7 p-2">
                    &nbsp;
                </div>
                <div className="col-md-2 p-3">
                    <div className="list mx-0 px-2">
                        <div>
                            <h4 className="pt-3"><b>Users:</b></h4>
                        </div>
                        <div className="user-list pb-5">
                            {users.map(user => (
                                <div key={user.id} className="user-row">
                                    <img src={user.profilePictureSrc} alt={user.userName}
                                         className="user-avatar"/>
                                    <p className="user-name">{user.userName}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
