import React, {useEffect, useState} from "react";
import {createAPIEndpoint, ENDPOINTS} from "../../api/axios";
import "./Home.css"
import Select from 'react-select';

export const Home = (props) => {
    const {createProject} = props
    const [errors, setErrors] = useState({})
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [userProfile, setUserProfile] = useState({});
    const [isProjectManager, setIsProjectManager] = useState(false);
    const users = allUsers.filter(user => user.id !== sessionStorage.getItem("userId"));
    const [selectedUsers, setSelectedUsers] = useState([]);
    const [projectData, setProjectData] = useState({
        title: '',
        projectMembers: [],
    });

    const jobTitles = {
        0: "Developer",
        1: "Tester",
        2: "Designer",
        3: "Business Analyst",
        4: "Project Manager",
        5: "Other"
    }

    const userOptions = users.map(user => ({
        value: user.id,
        label: user.userName,
    }));

    useEffect(() => {
        createAPIEndpoint(ENDPOINTS.userProjects).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProjects(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetch()
            .then(res => {
                setAllUsers(res.data)
            })
            .catch(err => {
                console.log(err);
            });

        createAPIEndpoint(ENDPOINTS.users).fetchById(sessionStorage.getItem("userId"))
            .then(res => {
                setUserProfile(res.data)
                setIsProjectManager(res.data.jobTitle === 4)
            })
            .catch(err => {
                console.log(err);
            })
    }, [])

    const handleChange = (e) => {
        const {name, value} = e.target;
        setProjectData({...projectData, [name]: value});
    };

    const handleUserSelectChange = (selectedOptions) => {
        projectData.projectMembers = selectedOptions
        setProjectData({...projectData, projectMembers:selectedOptions})
        //setSelectedUsers(selectedOptions);
        console.log(projectData)
    };

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(e.data)
    }

    const customStyles = {
        control: (provided, state) => ({
            ...provided,
            border: '2px solid #5F9DF7',
            borderRadius: '0.375rem',
            ':focus, :active': {
                boxShadow: '0 0 0 0.25rem rgba(23, 70, 162,0.25) !important',
                border: '2px solid #1746A2'
            },
        }),
    };

    return (
        <div>
            <div className="row mx-0">
                <div className="col-md-3 p-3">
                    <div className="card text-center">
                        <div className="card-body">
                            <img src={userProfile.profilePictureSrc} alt={userProfile.userName}
                                 className="avatar"/>
                            <h4 className="card-title dark-blue">{userProfile.userName}</h4>
                            <h6 className="card-text dark-blue">{jobTitles[userProfile.jobTitle]}</h6>
                            <div className="my-5">
                                {isProjectManager &&
                                    <div>
                                        {/* Step 2: Button to open the modal */}
                                        <button className="btn pill-btn mx-auto" data-bs-toggle="modal"
                                                data-bs-target="#staticBackdrop">
                                            + Add Project
                                        </button>
                                        <div className="modal fade" id="staticBackdrop" data-bs-backdrop="static"
                                             data-bs-keyboard="false" tabIndex="-1"
                                             aria-labelledby="staticBackdropLabel" aria-hidden="true">
                                            <div className="modal-dialog" role="document">
                                                <div className="modal-content">
                                                    <div className="modal-header">
                                                        <h3 className="modal-title fs-5 dark-blue"
                                                            id="staticBackdropLabel">Create a new
                                                            Project</h3>
                                                        <button type="button" className="btn-close"
                                                                data-bs-dismiss="modal" aria-label="Close"></button>
                                                    </div>
                                                    <form onSubmit={handleSubmit}>
                                                        <div className="modal-body">
                                                            <div className="form-floating my-2">
                                                                <input
                                                                    type="text"
                                                                    className="form-control input-border"
                                                                    id="projectTitle"
                                                                    name="title"
                                                                    placeholder="Project Title"
                                                                    value={projectData.title}
                                                                    onChange={handleChange}
                                                                    required
                                                                />
                                                                <label htmlFor="projectTitle" className="form-label">Project
                                                                    Title</label>
                                                            </div>
                                                            <div className="text-start my-3">
                                                                <Select
                                                                    options={userOptions}
                                                                    isMulti
                                                                    value={projectData.projectMembers}
                                                                    styles={customStyles}
                                                                    placeholder="Select Members"
                                                                    onChange={handleUserSelectChange}
                                                                />
                                                            </div>
                                                        </div>
                                                        <div className="modal-footer">
                                                            <button type="submit" className="btn btn-primary">
                                                                Save
                                                            </button>
                                                            <button type="button" className="btn btn-danger"
                                                                    data-bs-dismiss="modal">Close
                                                            </button>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>

                                    </div>
                                }
                                {!isProjectManager &&
                                    <p>&nbsp;</p>
                                }
                            </div>

                        </div>
                    </div>
                </div>
                <div className="col-md-7 p-3">
                    <h3 className="dark-blue">Projects:</h3>
                    {userProjects &&
                        <div>
                            {userProjects.map((project) => (
                                <div key={project.id}>
                                    <h5>{project.title}</h5>
                                    <p>Started: {project.dateStarted}</p>
                                    <h6>Assigned Users:</h6>
                                    <div className="user-list">
                                        {project.projectMembers.map((projectMember) => (
                                            // Render user profile picture here
                                            <img
                                                key={projectMember.id}
                                                src={projectMember.user.profilePictureSrc}
                                                alt={projectMember.user.userName}
                                                className="user-avatar"
                                            />
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    }
                </div>
                <div className="col-md-2 p-3">
                    <div className="list mx-0">
                        <div className="px-3">
                            <h4 className="pt-3 dark-blue"><b>Users:</b></h4>
                        </div>
                        <div className="user-list px-4 pb-5">
                            {users.map(user => (
                                <div key={user.id} className="user-row dark-blue">
                                    <img src={user.profilePictureSrc} alt={user.userName}
                                         className="user-avatar"/>
                                    <p className="user-name">{user.userName}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
